sys_variable_value.value:
try{
	var mapData={};
	var i=0;
	mapData.paramData =[];
	var tp;
	var gr=new GlideRecord('x_aetp_vyom_automa_ae_mapping');
	gr.addQuery('u_catalog_item',"!=",'');
	gr.addQuery('u_workflow_name',"!=",'');
	gr.query();
	var current_request_item=current.getDisplayValue('cat_item');
	var curr_num="%%STAGING_SYSID%%";
	var wkfl_name;
	var wkfl_url;
	var question_name;
	var json_field;
	while(gr.next()){
		var cat_item=gr.getDisplayValue('u_catalog_item');
		if(current_request_item==cat_item){
			wkfl_name= gr.getDisplayValue('u_workflow_name');
			wkfl_url= gr.getValue('u_workflow_url');
			json_field= gr.getValue('u_json');
		}
	}
	current.x_aetp_vyom_automa_workflowname=wkfl_name;
	var current_number=current.number;
	var array_data=[];
	var arr1=[];
	var question_sysid=[];
	var t=JSON.parse(json_field);
	var count = Object.keys(t).length;
	var p=0;
	var mtom_table=new GlideRecord('sc_item_option_mtom');
	mtom_table.addQuery('sc_item_option',"!=",' ');
	mtom_table.query();
	for (p = 0; p < count; p++) {
		var json_question=t.params[p].value;
		while(mtom_table.next()){
			var	mtom_request_number=mtom_table.getDisplayValue('request_item');
			if(current_number==mtom_request_number){
				var	question_value=mtom_table.getDisplayValue('sc_item_option');
				var gr2 = new GlideRecord('sc_item_option');
				gr2.addQuery('item_option_new',"!=",' ');
				gr2.query();
				while(gr2.next()){
					if(gr2.sys_id==question_value){
						var question2=gr2.getDisplayValue('item_option_new');
						var Answer = gr2.getValue('value');
						var param = {'question':question2, 'answer':Answer};
						mapData.paramData[i]=param;
						i++;
					}
				}
			}
		}
		var Final_ae_mapping_json="";
		tp=JSON.parse(json_field);
		for(var c=0;c<count;c++){
			var json=tp[c];
			var json_question=tp.params[c].value;
			for(var l=0;l<Object.keys(mapData.paramData).length;l++){
				var map_data=mapData.paramData[l].question;
				if(map_data==json_question){
					tp.params[c].value=mapData.paramData[l].answer;
					tp.sourceId=curr_num.toString();
					tp.userId= gs.getUserName();	
				}
			current.x_aetp_vyom_automa_json=JSON.stringify(tp);
			}
		}
	}
}catch(e){
	gs.debug("Error occured in Create JSON Payloads Workflow component : " + e);
}

