current.request.state=2;
answer=checkSeq();
function checkSeq(){
	var rs=new GlideRecord('sc_request');
	var st;
	rs.addQuery('sys_id',current.request);
	rs.query();
	if(rs.next())
	{
	rs.state=2;
	st=rs.state;
	}
	rs.update();
	if(current.x_aetp_vyom_automa_sequential==false && st==2){
	current.state=2;
	var gr=new GlideRecord('sc_task');
    gr.addQuery('request_item',current.sys_id);
    gr.query();
    while(gr.next()){
	gr.state=2;
	gr.update();
	 }
	return 'no';	
}
	return 'yes';
}

